## \file hypotez/src/bots/openai_bots/chatgpt-telegram/node_modules/fluent-ffmpeg/node_modules/.bin/which.ps1
# -*- coding: utf-8 -*-
#! venv/Scripts/python.exe

""" module: src.bots.openai_bots.chatgpt-telegram.node_modules.fluent-ffmpeg.node_modules..bin """
MODE = 'debug'
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../which/bin/which" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../which/bin/which" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../which/bin/which" $args
  } else {
    & "node$exe"  "$basedir/../which/bin/which" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
